import React, { useContext } from 'react'
import useInputState from './hooks/useInputState'
import { Link, Redirect } from 'react-router-dom'
import { Paper, ListItem, TextField, Grid, Button } from '@material-ui/core'
import fire from './Fire'
import { AuthContext } from './Auth'

const Login = ({ history }) => {
    const handleLogin = useCallback(async event => {
        event.preventDefault();
        const { email, password } = event.target.elements;
        try {
            await fire
                .auth()
                .signInWithEmailAndPassword(email.value, password.value);
            history.push("/")
        } catch (error) {
            alert("error")
        }
    }, [history]);

    const { currentUser } = useContext(AuthContext)

    if (currentUser) {
        return <Redirect to="/" />
    }

    return (
        <Grid container justify="flex-end" alignItems="center" direction="column">
            <Paper style={{ width: "20%", padding: "2rem" }}>
                <form onSubmit={handleLogin}>
                    <TextField name="email" id="outlined-basic" label="Username" variant="outlined" fullWidth />
                    <TextField id="outlined-password-input" label="Password" type="password" autoComplete="current-password" variant="outlined" fullWidth />
                    <Button type="submit" variant="contained" color="primary" style={{ margin: "1rem" }} >Login</Button>
                </form>
                <Link to="/signup" style={{ textDecoration: 'none' }} >Don't have an account?</Link>
            </Paper>
        </Grid>
    )
}

export default LoginForm
