import React, { useContext } from 'react'
import { AppBar, Typography, Button, Toolbar } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import { Link } from 'react-router-dom'
import { AuthContext } from './Auth'
import fire from './Fire'


const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    newHaiku: {
        flexGrow: 1,
    },
}));


function Header() {
    const classes = useStyles();

    const { currentUser } = useContext(AuthContext)
    return (
        <div className={classes.root}>
            <AppBar position="static">
                <Toolbar>
                    <Link to="/" style={{ textDecoration: "none", color: "white" }} edge="start" className={classes.menuButton}>Home</Link>
                    {currentUser !== null ?
                        <>
                            <Link to="/storyfield" style={{ textDecoration: "none", color: "white" }} className={classes.newHaiku}>Write a new haiku!</Link>
                            <Typography style={{ margin: "0 1rem" }}>Logged in as {currentUser.email}</Typography>
                            <Button onClick={() => fire.auth().signOut()} variant="contained" style={{ margin: "0 1rem" }}>Sign Out</Button>
                        </>
                        : null}
                </Toolbar>
            </AppBar>
        </div>
    )
}

export default Header
