import React, { useState } from 'react'
import firebase from '../Fire'

export const HaikuContext = React.createContext();
export function HaikuProvider(props) {
    const [initialHaikus, setInitial] = useState([]);
    const db = firebase.firestore();
    let cityRef = db.collection('haikus').doc('8LMQarulirmmNLL8dTqY');
    cityRef.get()
        .then(doc => {

            if (!doc.exists) {
                setInitial([])
            } else {
                setInitial(doc.data().haikus);
            }
        })
        .catch(err => {
            setInitial([])
        });



    const [haikus, setHaiku] = useState(initialHaikus)


    return (
        <HaikuContext.Provider value={{ haikus, setHaiku }}>
            {props.children}
        </HaikuContext.Provider>
    )
}
