import React from 'react'


function HaikuScore(id) {
    for (let i = 0; i < localStorage.length; i++) {
        if (localStorage.key(i).id === id) {
            localStorage.key(i).score = localStorage.key(i).score + 1
        }


    }


}
export default HaikuScore
