import React from 'react'



function HaikuScore(id) {
    const haikus = localStorage.getItem("haikus")

    for (let i = 0; i < haikus.length; i++) {
        if (haikus[i].id === id) {
            haikus[i].score = haikus[i].score + 1
        }
    }
    localStorage.setItem("haikus", haikus)




}
export default HaikuScore
