import React, { useContext } from 'react'
import { Paper } from '@material-ui/core'
import { HaikuContext } from './HaikuList'


export default function Haiku({ title, text, user, score, id }) {
    const { haikus, setHaiku } = useContext(HaikuContext)
    const handleHaiku = (id) => {
        const updatedHaikus = haikus.map(haiku => (
            haiku.id === id ? { ...haiku, score: haiku.score + 1 } : haiku)
        )
        setHaiku(updatedHaikus);

    }
    window.localStorage.setItem("haikus", JSON.stringify(haiku))

    return (
        <Paper style={{ display: "inline-block", margin: "1rem" }}>
            <div style={{ display: "flex", flexDirection: "column", width: "20%", margin: "1rem 1rem 0 1rem", fontSize: "2rem", float: "left" }}>
                <i style={{ color: "green" }} className="fas fa-thumbs-up" onClick={() => handleHaiku(id)}></i>
                <div>{score}</div>
                <i style={{ color: "pink" }} className="fas fa-thumbs-down"></i>
            </div>
            <h1 style={{}}>{title}</h1>
            <div style={{ margin: "1rem" }}>{text}</div>
            <div style={{ textAlign: "center", margin: "1rem", fontWeight: "bold" }}>-{user}</div>
        </Paper>
    )
}
