import React, { useContext, useEffect } from 'react'
import { Paper } from '@material-ui/core'
import { HaikuContext } from './HaikuList'

export default function Haiku({ title, text, user, score, id }) {
    const { haikus, setHaiku } = useContext(HaikuContext)
    const handleHaiku = (id, a) => {
        const updatedHaikus = haikus.map(haiku => (
            haiku.id === id ? { ...haiku, score: haiku.score + a } : haiku)
        )
        updatedHaikus.sort((a, b) => b.score - a.score);
        setHaiku(updatedHaikus);
    }
    window.localStorage.setItem("haikus", JSON.stringify(haikus))
    return (
        <Paper style={{ margin: "1rem" }}>
            <div style={{ display: "flex", flexDirection: "column", width: "15%", margin: "1rem 0 1rem 1rem", fontSize: "2rem", float: "left" }}>
                <i style={{ color: "green" }} className="fas fa-thumbs-up" onClick={() => handleHaiku(id, 1)}></i>
                <div >{score}</div>
                <i style={{ color: "pink" }} className="fas fa-thumbs-down" onClick={() => handleHaiku(id, -1)}></i>
            </div>
            <div style={{}}>
                <h2 style={{}}>{title}</h2>
                <div style={{}}>{text}</div>
                <div style={{ fontWeight: "bold" }}>-{user}</div>
            </div>

        </Paper>
    )
}
