import React, { useContext } from 'react'
import { HaikuContext } from './HaikuList'
import Haiku from './Haiku.js'

function Home() {
    const { haikus, setHaiku } = useContext(HaikuContext);
    return (
        <div>
            <h1 style={{ margin: "1rem" }}>Novelly</h1>
            {haikus.map(haiku => (
                <Haiku title={haiku.title} text={haiku.text} user={haiku.user} />
            ))}

        </div>
    )
}
export default Home
