import React, { useCallback } from 'react'
import useInputState from './hooks/useInputState'
import { Link, withRouter } from 'react-router-dom'
import { Paper, ListItem, TextField, Grid, Button } from '@material-ui/core'
import './SignUp.css'
import fire from './Fire'

const SignUp = ({ history }) => {
    const handleSignUp = useCallback(async event => {
        const { username, password } = event.target.elements;
        event.preventDefault();
        try {
            await fire
                .auth()
                .createUserWithEmailAndPassword(username, password);
            history.push("/")
        } catch (error) {
            alert("error")
        }
    }, [history])

    return (
        <div className="sign">
            <h2>Join Novelly!</h2>
            <h1>Create your account</h1>
            <Grid container justify="flex-end" alignItems="center" direction="column">
                <Paper style={{ width: "20%", padding: "2rem" }}>
                    <form onSubmit={handleSignUp}>
                        <TextField id="outlined-basic" label="Username" variant="outlined" value={name} fullWidth required />
                        <TextField id="outlined-password-input" label="Password" type="password" autoComplete="current-password" variant="outlined" value={password} fullWidth required />
                        <TextField id="outlined-password-input" label="Password again" type="password" autoComplete="current-password" variant="outlined" value={password} fullWidth required />
                        <Button variant="contained" color="primary" style={{ margin: "1rem" }} >Sign Up</Button>
                    </form>
                    <Link to="/" style={{ textDecoration: 'none' }} >Already have an account?</Link>
                    <p >*Please note that all user information will be leaked to third party companies </p>
                </Paper>
            </Grid>
        </div>
    )
}

export default withRouter(SignUp)
