import React, { useContext, useState } from 'react'
import { HaikuContext } from './HaikuList'
import Haiku from './Haiku.js'
import { FormContext } from './FormContext'
import { Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
        overflow: 'hidden',
        padding: theme.spacing(0, 3)
    },

}));

function Home() {
    const classes = useStyles();
    const { haikus, setHaiku } = useContext(HaikuContext);
    const { formSent, handleForm } = useContext(FormContext)
    return (
        <div>
            {formSent === true ? <h1 style={{ textAlign: "center" }}>Your haiku was created!</h1> :
                <> <h1 style={{ textAlign: "center" }}>Popular haikus</h1>
                    <div className={classes.root} >
                        <div style={{
                            position: "absolute",
                            top: "140px",
                            left: "0px",
                            right: "0px",
                            bottom: "0px",
                            overflowY: "scroll"
                        }}>
                            {haikus.map(haiku => (
                                <Haiku id={haiku.id} title={haiku.title} text={haiku.text} user={haiku.user} score={haiku.score} userVote={haiku.userVote} />
                            ))}
                        </div>
                    </div></>}
        </div>
    )
}
export default Home
