import React, { useContext, useState } from 'react'
import { HaikuContext } from './HaikuList'
import Haiku from './Haiku.js'
import { Grid } from '@material-ui/core';

function Home() {
    const { haikus, setHaiku } = useContext(HaikuContext);
    return (
        <div style={{ textAlign: "center" }}>
            <h2 >Popular haikus</h2>
            <div style={{ position: "absolute", overflowY: "scroll", top: "9rem", left: "0px", right: "0px", bottom: "0px" }}>
                <div style={{ flexDirection: "column", textAlign: "center" }} >
                    {haikus.map(haiku => (
                        <Haiku id={haiku.id} title={haiku.title} text={haiku.text} user={haiku.user} score={haiku.score} userVote={haiku.userVote} />
                    ))}
                </div>
            </div>
        </div>
    )
}
export default Home
