import React, { useContext } from 'react'
import { HaikuContext } from './HaikuList'
import Haiku from './Haiku.js'
import { FormContext } from './FormContext'
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
        overflow: 'hidden',
        padding: theme.spacing(0, 3)
    },

}));

function Home() {
    const classes = useStyles();
    const { haikus } = useContext(HaikuContext);
    const { formSent } = useContext(FormContext)
    return (
        <div >
            {formSent === true ? <div style={{ textAlign: "center" }}><h1 >Your haiku was created!</h1></div> :
                <> <div style={{ textAlign: "center" }}><h1 style={{ backgroun: "rgb(247,249,250)", color: "white", display: "inline-block", borderRadius: "15px", padding: "0.5rem", marginTop: "0.5rem", fontWeight: "400" }}>Popular haikus</h1></div>
                    <div className={classes.root} >
                        <div style={{ position: "absolute", top: "140px", left: "0px", right: "0px", bottom: "0px", overflowY: "scroll" }}>
                            {haikus.map(haiku => (
                                <Haiku key={haiku.id} id={haiku.id} title={haiku.title} text={haiku.text} user={haiku.user} score={haiku.score} userVote={haiku.userVote} />
                            ))}
                        </div>
                    </div></>}
        </div>
    )
}
export default Home
