import React, { useContext, useState } from 'react'
import { HaikuContext } from './HaikuList'
import Haiku from './Haiku.js'
import { Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
        overflow: 'hidden',
        padding: theme.spacing(0, 3)
    },

}));

function Home() {
    const classes = useStyles();
    const { haikus, setHaiku } = useContext(HaikuContext);
    return (
        <div>


            <div className={classes.root}>
                <h1 style={{ textAlign: "center", position: "fixed" }}>Popular haikus</h1>
                {haikus.map(haiku => (
                    <Haiku id={haiku.id} title={haiku.title} text={haiku.text} user={haiku.user} score={haiku.score} userVote={haiku.userVote} />
                ))}
            </div>
        </div>
    )
}
export default Home
