import React, { useContext, useState } from 'react'
import { HaikuContext } from './HaikuList'
import Haiku from './Haiku.js'
import { Grid } from '@material-ui/core';
import { makeStyles } from "@material-ui/core/styles";


const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1
    },
    paper: {
        padding: theme.spacing(1),
        textAlign: "center"

    }
}));

function Home() {
    const classes = useStyles();

    const { haikus, setHaiku } = useContext(HaikuContext);
    return (
        <div className={classes.root}>
            <Grid container spacing={1}>
                <Grid container item xs={12} spacing={3} justify='center' direction="column" alignItems="center" >
                    {haikus.map(haiku => (
                        <Haiku id={haiku.id} title={haiku.title} text={haiku.text} user={haiku.user} score={haiku.score} userVote={haiku.userVote} />
                    ))}
                </Grid>
            </Grid>
        </div>
    )
}
export default Home
