import React, { useContext } from 'react'
import { HaikuContext } from './HaikuList'
const HaikuScore = (id) => {
    const { haikus, setHaiku } = useContext(HaikuContext)
    const updatedHaikus = haikus.map(haiku =>
        haiku.id === id ? { ...haiku, score: haiku.score + 1 } : haiku)
    setHaiku(updatedHaikus);
}
export default HaikuScore
