import React, { useContext } from 'react'
import { HaikuContext } from './HaikuList'


function HaikuScore(id) {
    const { haikus, setHaiku } = useContext(HaikuContext)

    haikus.map(haiku => (
        haiku.id === id ?
            haiku.score = haiku.score + 1 : haiku.score = null
    ))



}
export default HaikuScore
