import React, { useContext } from 'react'
import { AppBar, Typography, Button, Toolbar } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import { Link } from 'react-router-dom'
import { AuthContext } from './Auth'
import fire from './Fire'
import { mergeClasses } from '@material-ui/styles';

function Header() {
    const useStyles = makeStyles(theme => ({
        root: {
            flexGrow: 1,
        },
        menuButton: {
            marginRight: theme.spacing(2),
        },
        title: {
            flexGrow: 1,
        },
    }));
    const { currentUser } = useContext(AuthContext)
    return (
        <div className={classes.root}>
            <AppBar position="static">
                <Toolbar>
                    <Link to="/" style={{ textDecoration: "none", color: "white", margin: "0 1rem" }}>Home</Link>
                    {currentUser !== null ?
                        <>
                            <Link to="/storyfield" style={{ textDecoration: "none", color: "white", margin: "0 1rem" }}>Write a new haiku!</Link>
                            <Typography style={{ margin: "0 1rem" }}>Logged in as {currentUser.email}</Typography>
                            <Button onClick={() => fire.auth().signOut()} variant="contained" style={{ margin: "0 1rem" }}>Sign Out</Button>
                        </>
                        : null}
                </Toolbar>
            </AppBar>
        </div>
    )
}

export default Header
