import React, { useContext } from 'react'
import { Paper } from '@material-ui/core'
import { HaikuContext } from './HaikuList'
import { AuthContext } from './Auth'
import { Grid } from '@material-ui/core'
import syllable from 'syllable'

export default function Haiku({ title, text, user, score, id }) {
    const { currentUser } = useContext(AuthContext);
    const { haikus, setHaiku } = useContext(HaikuContext)

    function chopHaiku(evt, start, syl) {
        var haiku = "";
        var index = "";
        for (let i = 0; i < evt.length; i++) {

            if (syllable(index) === start) {
                break;
            }
            index = index + evt[i];
        }
        for (let i = index.length; i < evt.length; i++) {
            haiku = haiku + evt[i];
            if (syllable(haiku) === syl) {
                haiku = haiku + '\n';
                break;
            }
        }
        return haiku
    }

    const handleHaiku = (id, a) => {
        //making sure that user can only have one vote for haiku
        const updatedHaikus = haikus.map(haiku => (
            haiku.id === id && haiku.userVote.includes(currentUser.email) != true ? { ...haiku, score: haiku.score + a, userVote: [...haiku.userVote, currentUser.email] } : haiku)
        )
        updatedHaikus.sort((a, b) => b.score - a.score);
        setHaiku(updatedHaikus);
    }
    window.localStorage.setItem("haikus", JSON.stringify(haikus))
    return (
        <Grid item xs={12} sm={6} style={{ margin: "0.5rem", width: "25%" }}>
            <Paper >
                <div style={{ display: "flex", flexDirection: "column", fontSize: "2rem", backgroundColor: "rgb(247,249,250)", float: "left", margin: "0.5rem" }}>
                    <i style={{ color: "green", margin: "0 auto" }} className="fas fa-thumbs-up" onClick={() => handleHaiku(id, 1)}></i>
                    <div style={{ margin: "0 auto" }}>{score}</div>
                    <i style={{ color: "pink", margin: "0 auto" }} className="fas fa-thumbs-down" onClick={() => handleHaiku(id, -1)}></i>
                </div>
                <div style={{ textAlign: "center", margin: "1rem" }}>
                    <h2>"{title}"</h2>
                    <div>{chopHaiku(text, 0, 5)} </div>
                    <div>{chopHaiku(text, 5, 7)} </div>
                    <div>{chopHaiku(text, 11, 5)} </div>

                    <div style={{ fontSize: "0.8rem", marginTop: "0.5rem", marginBottom: "0.5rem", fontWeight: "bold" }}>-{user}</div>
                </div>

            </Paper>
        </Grid>

    )
}
